let lineProtocol = '';

for (let sensor of msg.payload.sensors) {
    let sensorId = sensor.sensor_id.replace(/ /g, '\\ ').replace(/,/g, '\\,');
    let microId = msg.payload.micro_id.replace(/ /g, '\\ ').replace(/,/g, '\\,');

    lineProtocol += `sonido,sensor_id=${sensorId},micro_id=${microId} valor=${sensor.value}\n`;
}
msg.payload = lineProtocol;

return msg;
